var bodyParser = require('body-parser'),
    express = require("express"),
    path    = require("path");

global.workDir = path.join(path.dirname(require.main.filename), '..');

module.exports = function(app) {
    var dirAlias =
    {
        '/libs': 'site/libs/js',
        '/js' : 'site/js',
        '/css' : 'site/libs/css',
        '/csscustom' : 'site/css',
        '/' : '/'
    }

    for(var e in dirAlias) {
        app.use(e,express.static(path.join(global.workDir, dirAlias[e])));
    }
    app.use(bodyParser.urlencoded({
        extended: false
    }));
    app.use(bodyParser.json());
}
